import React from 'react';

function Modal(props) {

    const modalContainer = {
        // background: 'rgba(44,42,41)',
        background: 'rgba(63, 73, 83, 1)',
        minHeight: '300px',
        minWidth: '250px',
        height: '40vw',
        maxHeight: '450px',
        color: '#fff',
        margin: '0 20px 0 20px',
        borderRadius: '5px',
        display: 'flex',
        flexDirection: 'column',
        // border: '1px solid black',
        // boxShadow: '5px 5px #888',
    }

    const imageContainer = {
        width: '99%',
        height: '40%',
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        border: '1px solid #151C29',
        borderTopLeftRadius: 'inherit',
        borderTopRightRadius: 'inherit',
    }

    const contentContainer = {
        height: '60%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',

    }

    const heading = {
        fontSize: '120%',
        margin: 0,
        padding: '5px',
        color: '#fff',
        textShadow: '1px 1px rgba( 0, 0, 0, .5 )',
    }

    const text = {
        flex: '2',
        fontSize: '100%',
        padding: '8px',
        color: '#fff',
    }

    const buttonCont = {
        padding: '10px',
    }

    const button = {
        background: 'rgb(166, 25, 46)',
        color: 'white',
        fontSize: '1em',
        fontWeight: '600',
        height: '50%',
        minHeight: '25px',
        padding: '0 20%',
        // border: '1px solid rgb(44,42,41)',
        borderRadius: '5px',
    }

  return (
    <>    
        <div style={ modalContainer }>
            <a href={props.link} style={ imageContainer }>&nbsp;</a>  
            <div style={ contentContainer }>
                <h1  style={ heading }>
                    {props.heading}
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
