import React from 'react';
import EmployeeCard from './EmployeeCard';

function ContactSlip() {
// 151C29,647692,4199E0,4CB1F3

    const contactSlipCont = {
        background: '#4199E0',
        color: '#eee',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '30vw',
        maxHeight: '800px',

        padding: '2vw 0 2vw 2vw',
    }

    const leftBlock = {
        width: '50%',
        height: '100%',
        borderRight: '1px solid #151C29',
        display: 'flex',
        flexDirection: 'column',


    }

    const leftBlockContent = {
        flex: '2',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-around',
        margin: '4vw 0',

    }

    const phone = {
        fontSize: '2em',
    }

    const address = {
        // backgroundColor: '#647692',
        borderRadius: '20px',
        display: 'flex',

    }

    const rightBlock = {
        width: '50%',
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
    }

    const topBlock = {
        backgroundColor: '#151C29',
        display: 'flex',
        alignItems: 'center',
        height: '50%',
        margin: '0 0 10px 30px',
        borderRadius: '10px 0 0 10px',
        padding: '5px 10px',
    }

    const bottomBlock = {
        backgroundColor: '#151C29',
        display: 'flex',
        alignItems: 'center',
        height: '50%',
        margin: '10px 0 0 30px',
        borderRadius: '10px 0 0 10px',
        padding: '5px 10px',
    }

    // const imgContTop = {
    //     background: `url('./images/jack.jpg')`,
    //     backgroundRepeat: 'no-repeat',
    //     backgroundSize: 'cover',
    //     display: 'block',
    //     height: '100%',
    //     width: '15vw',
    //     borderRadius: '50%',
    // }

    const imgContBottom = {
        background: `url('./images/john.jpg')`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        display: 'block',
        height: '100%',
        width: '15vw',
        borderRadius: '50%',
    }

    // const contDesc = {
    //     height: '100%',
    //     width: '60%',
    //     display: 'flex',
    //     flexDirection: 'column',
    //     justifyContent: 'space-between',
    // }

    // const nameDesc = {
    //     display: 'flex',
    //     flexDirection: 'column',
    // }

    // const contactDesc = {
    //     fontSize: '2vw',
    //     fontWeight: '600',
    //     letterSpacing: '1px',
    //     margin: '0 0 5vh 0',
    // }

  return (
    <div className="contactSlip" style={ contactSlipCont }>
        <div style={ leftBlock }>
            <div style={{flex: '1',fontSize: '4.5vw',fontWeight: '600'}}>Contact Us</div>
            <div style={ leftBlockContent }>
                <div style={ phone }><b>Phone:</b> 703-865-2514 </div>
                <div style={ address }>
                    <div style={{width: '50%',fontSize: '2em'}}><b>Address:</b></div>
                    <div>774-A Walker Road <br/>
                    Great Falls, VA 22066</div>
                </div>
            </div>
        </div>
        <div style={ rightBlock }>
            <div style={ topBlock } className="bl">
                <EmployeeCard image="./images/jack.jpg" name="Jack Mencia" title="President and CEO, CBSI" email="jack@cbsi.co"/>
            </div>
            <div style={ bottomBlock }>
                <EmployeeCard image="./images/john.jpg" name="John Short" title="Operations Director, CBSI" email="john@cbsi.co"/>
            </div>
        </div>
    </div>
  );
}

export default ContactSlip;
