import React from 'react';

const aboutHeading = {
    fontSize: '4vw',
}

const aboutText = {
    fontSize: '2vw',
    lineHeight: '1vw',
}

function BasicInfoBlock(props) {

    return (
        <div className="basicInfoBlock">
            <h1 style={ aboutHeading }> {props.heading || 'headingContent Required'} </h1>
            <div style={ aboutText }>
                {props.textContent || 'textContent Required'}
            </div>
        </div>
    );
}

export default BasicInfoBlock;
