import React from 'react';
import ProjectsData from './data/projects.json';
import BasicInfoBlock from './components/BasicInfoBlock';
import ResponsiveGrid from './components/ResponsiveGrid';
import Footer from './components/Footer';

const aboutBannerImg = {
    backgroundImage: 'url("/images/hilton.jpg")',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: '30vh',
    margin: '15px 0 0 0',
}

const bannerTxt = {
    color: '#fff',
    margin: '3vw 0 0 0',
    fontFamily: 'arial, san-serif',
    fontSize: '4.5vw',
    fontWeight: '100',
    textAlign: 'left',
}

const textCont1 = {
    background: 'rgba(26,56,111,.5)',
    padding: '6vw 0 20px 6vw',
    flex: '1',

}
const textCont2 = {
    display: 'block',
    background: 'rgba(100,100,100,.5)',
    flex: '1',
}

function Projects() {

    var dataArr = [];
    Object.keys(ProjectsData).forEach(key => dataArr.push(ProjectsData[key]));

    return (
        <div className="content">
            <div className="banner" style={ aboutBannerImg }>
                <div style={ textCont1 }>
                    <h2 style={ bannerTxt }>
                        Our Expertise
                        <br/>
                        <b style={{fontWeight: '600'}}>Speaks</b>
                        <br/>
                        For Itself.
                    </h2>
                </div>
                <div style={ textCont2 }></div>
            </div>

            <BasicInfoBlock 
                heading="Our Expertise Speaks For Itself."
                subHeading="
                    We take pride in the work we do, always putting our client's vision at the forefront, and emphasising 
                    the design process for optimum outcomes on every project.
                "
                text="
                    Take a look at some of the projects CBSI has had the privelage to be a part of!
                "
            />

            <ResponsiveGrid jsonData={ ProjectsData } />

            {
                dataArr.map((item, i) => {
                    var dataList = '';

                    if(item.list){

                        dataList += i < dataArr.length-1 
                            ? '<ul>'
                            : '<ul style="list-style-type: none">';

                        for(var j = 0; j < item.list.length; j++){
                            dataList += '<li>' + item.list[j] + '</li>';
                        }

                        dataList += '</ul>';
                    }

                    return item.image !== "" ? 
                        <ImageInfoBlock 
                            key={item.id}
                            id={item.heading}
                            isDark={ i%2!==0 } 
                            flip={ i%2===0  }
                            image={item.image} 
                            headerContent={item.heading} 
                            textContent={ 
                                item.longText || <div dangerouslySetInnerHTML={{__html: dataList}}></div>
                            }
                        />
                    : 
                        <div key={item.id} style={{padding: '4vw',}} id={item.heading}>
                            <h1>{item.heading}:</h1>
                            <div dangerouslySetInnerHTML={{__html: dataList}} style={{fontWeight: '600', lineHeight: '30px'}}></div>
                        </div>
                    ;
                })
            }

            <Footer />

        </div>
    );
}

export default Projects;
