import React from 'react';

const basicInfoBlock = {
    // background: 'cyan',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '8vw 0',
}

const headingCont = {
    // background: 'yellow',
    color: 'rgb(166, 25, 46)',
    fontSize: '3.5vw',
    fontWeight: '300',
    padding: '1vw 2vw 3vw 2vw',
    wordSpacing: '2vw',
}

const subHeadingCont = {
    // background: 'green',
    fontSize: '2.5vw',
    fontWeight: '300',
    padding: '1vw 1vw 3vw 1vw',
    width: '90vw',
}

const textCont = {
    // background: 'red',
    fontSize: '2vw',
    width: '90vw',
    lineHeight: '3vw',
    padding: '1vw',
}

function BasicInfoBlock(props) {

    return (
        <div style={ basicInfoBlock } className="basicInfoBlock">
            <div style={ headingCont }> 
                { props.heading || 'Heading Prop Required' } 
            </div>
            <div style={ subHeadingCont }> 
                { props.subHeading } 
            </div>
            <div style={ textCont }>
                { props.text }
            </div>
        </div>
    );
}

export default BasicInfoBlock;
