import React, { useState, useEffect } from 'react';
import './App.css'
import Nav from './components/Nav';
import Home from './Home'
import About from './About';
import Services from './Services';
import Projects from './Projects';
import Contact from './Contact';
import { BrowserRouter as Router, Route } from 'react-router-dom';

function App() {
  var [prevScrollPos, setPrevScrollPos] = useState(0);
  var prevScrollPos = window.pageYOffset;

  useEffect(() => {
    window.addEventListener('scroll', handleScroll(state.prevScrollPos))
  });

  function handleScroll(offset) {
    console.log('hello');
    var currentScrollPos = window.pageYOffset;
    var targetElement = document.getElementById('navCont');
    console.log("target Element: "+targetElement)
    if(targetElement != null){
        if((prevScrollPos > currentScrollPos)) {
            targetElement.style.top = "0";
        } else {
            targetElement.style.bottom = "0";
        }
    }
    // setPrevScrollPos(currentScrollPos);
    prevScrollPos = currentScrollPos;
  }

  return (
    <>
    <Router>
      <div className="App">
        <Nav onScroll={ handleScroll() }/>
        <Route path="/" exact component={Home} />
        <Route path="/about" component={About} />
        <Route path="/services" component={Services} />
        <Route path="/projects" component={Projects} />
        <Route path="/contact" component={Contact} />
      </div>
    </Router>
    </>
  );
}

export default App;
